INSERT INTO `ok_lang_deliveries` (`lang_id`, `delivery_id`, `name`, `description`) VALUES
(1,	1,	'Курьерская доставка по Москве',	'<p>Курьерская доставка осуществляется на следующий день после оформления заказа, если товар есть в наличии. Курьерская доставка осуществляется ежедневно с 10.00 до 21.00. Заказ на сумму свыше 5000 рублей доставляется бесплатно.&nbsp;<br /><br />Стоимость бесплатной доставки раcсчитывается от суммы заказа с учтенной скидкой. В случае если сумма заказа после применения скидки менее 5000 р., осуществляется платная доставка.&nbsp;<br /><br />При сумме заказа менее 5000 рублей стоимость доставки составляет от 300 рублей.</p>'),
